#include<bits/stdc++.h>using namespace std;int main() {     ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);    #ifndef ONLINE_JUDGE        freopen("input.in","r",stdin);        freopen("output.in","w",stdout);    #endif    int t;    cin >> t;    while(t--){        int n, m;        cin >> n >> m;        cout << "=======" << endl;        for(int a = 1; a <= n; a++){            for(int b = 1; b <= n; b++){                if(a < b){                    int lhs = (m % a) % b;                    int rsh = (m % b) % a;                    if(lhs == rsh){                        cout << a << " " << b << endl;                        cout << lhs << endl;                        cout << rsh << endl;                    }                }            }        }    }  return 0; } /* (6 % 2) % 5 = 0 (6 % 5) % 2 =  */