#include<bits/stdc++.h>using namespace std;int main() {     ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);    #ifndef ONLINE_JUDGE        freopen("input.in","r",stdin);        freopen("output.in","w",stdout);    #endif    int t;    cin >> t;    while(t--){        int n;        cin >> n;        if(n == 1){            cout << "NO" << endl;            continue;        }        while( n % 2 == 0){            n = n / 2;        }        int sqroot = sqrt(n);        if(sqroot * sqroot == n){            cout << "YES" << endl;        }else{            cout << "NO" << endl;        }            }  return 0; }